contracts/core/GameDAOModule.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "../interfaces/IGameDAOModule.sol";
import "../interfaces/IGameDAORegistry.sol";

/**
 * @title GameDAOModule
 * @dev Base contract for all GameDAO modules
 * @author GameDAO AG
 */
abstract contract GameDAOModule is IGameDAOModule, AccessControl, Pausable, ReentrancyGuard {
    // Role definitions
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");

    // State variables
    address private _registry;
    bool private _initialized;
    string private _version;

    // Events
    event ModuleInitialized(address indexed registry);
    event ModuleEnabled();
    event ModuleDisabled();

    // Errors
    error ModuleAlreadyInitialized();
    error ModuleNotInitialized();
    error OnlyRegistry();
    error InvalidRegistryAddress();

    /**
     * @dev Modifier to restrict access to the registry
     */
    modifier onlyRegistry() {
        if (_msgSender() != _registry) revert OnlyRegistry();
        _;
    }

    /**
     * @dev Modifier to ensure the module is initialized
     */
    modifier onlyInitialized() {
        if (!_initialized) revert ModuleNotInitialized();
        _;
    }

    /**
     * @dev Constructor
     * @param version_ The version of this module
     */
    constructor(string memory version_) {
        _version = version_;

        // Grant admin role to deployer initially
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(ADMIN_ROLE, _msgSender());
    }

    /**
     * @dev Returns the unique identifier for this module
     * @return bytes32 The module identifier
     */
    function moduleId() external pure virtual returns (bytes32);

    /**
     * @dev Returns the version of this module
     * @return string The version string
     */
    function version() external view returns (string memory) {
        return _version;
    }

    /**
     * @dev Initializes the module with the registry address
     * @param registryAddress The address of the GameDAO registry
     */
    function initialize(address registryAddress) external {
        if (_initialized) revert ModuleAlreadyInitialized();
        if (registryAddress == address(0)) revert InvalidRegistryAddress();

        _registry = registryAddress;
        _initialized = true;

        // Grant registry admin permissions
        _grantRole(ADMIN_ROLE, registryAddress);
        _grantRole(OPERATOR_ROLE, registryAddress);

        // Call internal initialization hook
        _onInitialize();

        emit ModuleInitialized(registryAddress);
    }

    /**
     * @dev Called when the module is enabled in the registry
     */
    function onModuleEnabled() external onlyRegistry onlyInitialized {
        _onModuleEnabled();
        emit ModuleEnabled();
    }

    /**
     * @dev Called when the module is disabled in the registry
     */
    function onModuleDisabled() external onlyRegistry onlyInitialized {
        _onModuleDisabled();
        emit ModuleDisabled();
    }

    /**
     * @dev Returns whether the module is initialized
     * @return bool True if initialized
     */
    function isInitialized() external view returns (bool) {
        return _initialized;
    }

    /**
     * @dev Returns the registry address
     * @return address The registry address
     */
    function registry() external view returns (address) {
        return _registry;
    }

    /**
     * @dev Returns the registry contract
     * @return IGameDAORegistry The registry contract
     */
    function getRegistry() internal view onlyInitialized returns (IGameDAORegistry) {
        return IGameDAORegistry(_registry);
    }

    /**
     * @dev Checks if another module is enabled
     * @param targetModuleId The module ID to check
     * @return bool True if the module is enabled
     */
    function isModuleEnabled(bytes32 targetModuleId) internal view onlyInitialized returns (bool) {
        return getRegistry().isModuleEnabled(targetModuleId);
    }

    /**
     * @dev Gets the address of another module
     * @param targetModuleId The module ID to get
     * @return address The module address
     */
    function getModule(bytes32 targetModuleId) internal view onlyInitialized returns (address) {
        return getRegistry().getModule(targetModuleId);
    }

    /**
     * @dev Pauses the module (admin only)
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpauses the module (admin only)
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Internal hook called during initialization
     * Override this in derived contracts to add custom initialization logic
     */
    function _onInitialize() internal virtual {}

    /**
     * @dev Internal hook called when the module is enabled
     * Override this in derived contracts to add custom enable logic
     */
    function _onModuleEnabled() internal virtual {}

    /**
     * @dev Internal hook called when the module is disabled
     * Override this in derived contracts to add custom disable logic
     */
    function _onModuleDisabled() internal virtual {}

    /**
     * @dev Internal function to update the module version
     * @param newVersion The new version string
     */
    function _updateVersion(string memory newVersion) internal {
        _version = newVersion;
    }

    /**
     * @dev Emergency pause function that can be called by the registry
     */
    function emergencyPause() external onlyRegistry {
        _pause();
    }

    /**
     * @dev Emergency unpause function that can be called by the registry
     */
    function emergencyUnpause() external onlyRegistry {
        _unpause();
    }

    /**
     * @dev Override to ensure only initialized modules can be paused
     */
    function _pause() internal virtual override onlyInitialized {
        super._pause();
    }

    /**
     * @dev Override to ensure only initialized modules can be unpaused
     */
    function _unpause() internal virtual override onlyInitialized {
        super._unpause();
    }
}

contracts/core/GameDAORegistry.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "../interfaces/IGameDAORegistry.sol";
import "../interfaces/IGameDAOModule.sol";

/**
 * @title GameDAORegistry
 * @dev Central registry for managing all GameDAO protocol modules
 * @author GameDAO AG
 */
contract GameDAORegistry is IGameDAORegistry, AccessControl, Pausable, ReentrancyGuard {
    using EnumerableSet for EnumerableSet.Bytes32Set;

    // Role definitions
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MODULE_MANAGER_ROLE = keccak256("MODULE_MANAGER_ROLE");

    // Module information
    struct ModuleInfo {
        address moduleAddress;
        string version;
        bool isEnabled;
        uint256 registeredAt;
        uint256 enabledAt;
    }

    // Storage
    mapping(bytes32 => ModuleInfo) private _modules;
    EnumerableSet.Bytes32Set private _allModules;
    EnumerableSet.Bytes32Set private _enabledModules;

    // Events (others inherited from IGameDAORegistry)
    event RegistryInitialized(address indexed admin);

    // Errors
    error ModuleNotRegistered(bytes32 moduleId);
    error ModuleAlreadyRegistered(bytes32 moduleId);
    error ModuleAlreadyEnabled(bytes32 moduleId);
    error ModuleNotEnabled(bytes32 moduleId);
    error InvalidModuleAddress();
    error ModuleInitializationFailed();

    /**
     * @dev Constructor
     * @param admin The address that will be granted the admin role
     */
    constructor(address admin) {
        require(admin != address(0), "Invalid admin address");

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(ADMIN_ROLE, admin);
        _grantRole(MODULE_MANAGER_ROLE, admin);

        emit RegistryInitialized(admin);
    }

    /**
     * @dev Registers a new module
     * @param moduleAddress The address of the module contract
     */
    function registerModule(address moduleAddress)
        external
        onlyRole(MODULE_MANAGER_ROLE)
        whenNotPaused
        nonReentrant
    {
        if (moduleAddress == address(0)) revert InvalidModuleAddress();

        // Get module info from the contract
        IGameDAOModule module = IGameDAOModule(moduleAddress);
        bytes32 moduleId = module.moduleId();
        string memory version = module.version();

        if (_modules[moduleId].moduleAddress != address(0)) {
            revert ModuleAlreadyRegistered(moduleId);
        }

        // Initialize the module
        try module.initialize(address(this)) {
            // Registration successful
        } catch {
            revert ModuleInitializationFailed();
        }

        // Store module information
        _modules[moduleId] = ModuleInfo({
            moduleAddress: moduleAddress,
            version: version,
            isEnabled: false,
            registeredAt: block.timestamp,
            enabledAt: 0
        });

        _allModules.add(moduleId);

        emit ModuleRegistered(moduleId, moduleAddress, version);
    }

    /**
     * @dev Enables a module
     * @param moduleId The unique identifier of the module
     */
    function enableModule(bytes32 moduleId)
        external
        onlyRole(MODULE_MANAGER_ROLE)
        whenNotPaused
        nonReentrant
    {
        ModuleInfo storage moduleInfo = _modules[moduleId];
        if (moduleInfo.moduleAddress == address(0)) revert ModuleNotRegistered(moduleId);
        if (moduleInfo.isEnabled) revert ModuleAlreadyEnabled(moduleId);

        moduleInfo.isEnabled = true;
        moduleInfo.enabledAt = block.timestamp;
        _enabledModules.add(moduleId);

        // Notify the module
        IGameDAOModule(moduleInfo.moduleAddress).onModuleEnabled();

        emit ModuleEnabled(moduleId);
    }

    /**
     * @dev Disables a module
     * @param moduleId The unique identifier of the module
     */
    function disableModule(bytes32 moduleId)
        external
        onlyRole(MODULE_MANAGER_ROLE)
        whenNotPaused
        nonReentrant
    {
        ModuleInfo storage moduleInfo = _modules[moduleId];
        if (moduleInfo.moduleAddress == address(0)) revert ModuleNotRegistered(moduleId);
        if (!moduleInfo.isEnabled) revert ModuleNotEnabled(moduleId);

        moduleInfo.isEnabled = false;
        moduleInfo.enabledAt = 0;
        _enabledModules.remove(moduleId);

        // Notify the module
        IGameDAOModule(moduleInfo.moduleAddress).onModuleDisabled();

        emit ModuleDisabled(moduleId);
    }

    /**
     * @dev Upgrades a module to a new implementation
     * @param moduleId The unique identifier of the module
     * @param newModuleAddress The address of the new module implementation
     */
    function upgradeModule(bytes32 moduleId, address newModuleAddress)
        external
        onlyRole(ADMIN_ROLE)
        whenNotPaused
        nonReentrant
    {
        if (newModuleAddress == address(0)) revert InvalidModuleAddress();

        ModuleInfo storage moduleInfo = _modules[moduleId];
        if (moduleInfo.moduleAddress == address(0)) revert ModuleNotRegistered(moduleId);

        address oldAddress = moduleInfo.moduleAddress;

        // Get new module info
        IGameDAOModule newModule = IGameDAOModule(newModuleAddress);
        require(newModule.moduleId() == moduleId, "Module ID mismatch");
        string memory newVersion = newModule.version();

        // Disable old module if enabled
        bool wasEnabled = moduleInfo.isEnabled;
        if (wasEnabled) {
            moduleInfo.isEnabled = false;
            _enabledModules.remove(moduleId);
            IGameDAOModule(oldAddress).onModuleDisabled();
        }

        // Initialize new module
        try newModule.initialize(address(this)) {
            // Initialization successful
        } catch {
            revert ModuleInitializationFailed();
        }

        // Update module info
        moduleInfo.moduleAddress = newModuleAddress;
        moduleInfo.version = newVersion;

        // Re-enable if it was enabled before
        if (wasEnabled) {
            moduleInfo.isEnabled = true;
            moduleInfo.enabledAt = block.timestamp;
            _enabledModules.add(moduleId);
            newModule.onModuleEnabled();
        }

        emit ModuleUpgraded(moduleId, oldAddress, newModuleAddress, newVersion);
    }

    /**
     * @dev Returns the address of a module
     * @param moduleId The unique identifier of the module
     * @return address The module address
     */
    function getModule(bytes32 moduleId) external view returns (address) {
        return _modules[moduleId].moduleAddress;
    }

    /**
     * @dev Returns whether a module is enabled
     * @param moduleId The unique identifier of the module
     * @return bool True if the module is enabled
     */
    function isModuleEnabled(bytes32 moduleId) external view returns (bool) {
        return _modules[moduleId].isEnabled;
    }

    /**
     * @dev Returns the version of a module
     * @param moduleId The unique identifier of the module
     * @return string The module version
     */
    function getModuleVersion(bytes32 moduleId) external view returns (string memory) {
        return _modules[moduleId].version;
    }

    /**
     * @dev Returns detailed information about a module
     * @param moduleId The unique identifier of the module
     * @return moduleInfo The complete module information
     */
    function getModuleInfo(bytes32 moduleId) external view returns (ModuleInfo memory) {
        return _modules[moduleId];
    }

    /**
     * @dev Returns all registered module IDs
     * @return bytes32[] Array of module IDs
     */
    function getAllModules() external view returns (bytes32[] memory) {
        return _allModules.values();
    }

    /**
     * @dev Returns all enabled module IDs
     * @return bytes32[] Array of enabled module IDs
     */
    function getEnabledModules() external view returns (bytes32[] memory) {
        return _enabledModules.values();
    }

    /**
     * @dev Returns the number of registered modules
     * @return uint256 The number of registered modules
     */
    function getModuleCount() external view returns (uint256) {
        return _allModules.length();
    }

    /**
     * @dev Returns the number of enabled modules
     * @return uint256 The number of enabled modules
     */
    function getEnabledModuleCount() external view returns (uint256) {
        return _enabledModules.length();
    }

    /**
     * @dev Pauses the registry (admin only)
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpauses the registry (admin only)
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Emergency function to disable all modules (admin only)
     */
    function emergencyDisableAllModules() external onlyRole(ADMIN_ROLE) {
        bytes32[] memory enabledModules = _enabledModules.values();

        for (uint256 i = 0; i < enabledModules.length; i++) {
            bytes32 moduleId = enabledModules[i];
            ModuleInfo storage moduleInfo = _modules[moduleId];

            moduleInfo.isEnabled = false;
            moduleInfo.enabledAt = 0;
            _enabledModules.remove(moduleId);

            // Notify the module
            try IGameDAOModule(moduleInfo.moduleAddress).onModuleDisabled() {
                // Module notified successfully
            } catch {
                // Continue with other modules even if one fails
            }

            emit ModuleDisabled(moduleId);
        }
    }
}

contracts/core/Treasury.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title Treasury
 * @dev Treasury contract for managing organization funds
 * @author GameDAO AG
 */
contract Treasury is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // Role definitions
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant SPENDER_ROLE = keccak256("SPENDER_ROLE");
    bytes32 public constant DEPOSITOR_ROLE = keccak256("DEPOSITOR_ROLE");

    // Treasury owner (the organization)
    bytes32 public immutable organizationId;
    address public immutable controlModule;

    // Balances tracking
    mapping(address => uint256) private _balances;
    address[] private _supportedTokens;
    mapping(address => bool) private _tokenSupported;

    // Spending limits
    mapping(address => uint256) private _spendingLimits; // per token
    mapping(address => mapping(address => uint256)) private _dailySpent; // spender => token => amount
    mapping(address => mapping(address => uint256)) private _lastSpendDay; // spender => token => day

    // Events
    event FundsDeposited(
        address indexed token,
        address indexed from,
        uint256 amount,
        string purpose,
        uint256 timestamp
    );

    event FundsSpent(
        address indexed token,
        address indexed to,
        address indexed spender,
        uint256 amount,
        string purpose,
        uint256 timestamp
    );

    event TokenAdded(address indexed token, uint256 timestamp);

    event TokenRemoved(address indexed token, uint256 timestamp);

    event SpendingLimitUpdated(
        address indexed token,
        uint256 oldLimit,
        uint256 newLimit,
        uint256 timestamp
    );

    // Errors
    error OnlyControlModule();
    error TokenNotSupported(address token);
    error InsufficientBalance(address token, uint256 requested, uint256 available);
    error SpendingLimitExceeded(address token, uint256 requested, uint256 limit);
    error ZeroAmount();
    error ZeroAddress();
    error TokenAlreadySupported(address token);

    /**
     * @dev Constructor
     * @param _organizationId The ID of the organization this treasury belongs to
     * @param _controlModule The address of the control module
     * @param _admin The admin address for this treasury
     */
    constructor(
        bytes32 _organizationId,
        address _controlModule,
        address _admin
    ) {
        require(_controlModule != address(0), "Invalid control module");
        require(_admin != address(0), "Invalid admin");

        organizationId = _organizationId;
        controlModule = _controlModule;

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ADMIN_ROLE, _admin);
        _grantRole(SPENDER_ROLE, _controlModule);
        _grantRole(DEPOSITOR_ROLE, _controlModule);
    }

    /**
     * @dev Modifier to restrict access to the control module
     */
    modifier onlyControlModule() {
        if (_msgSender() != controlModule) revert OnlyControlModule();
        _;
    }

    /**
     * @dev Add a supported token
     * @param token The token address to add
     */
    function addToken(address token) external onlyRole(ADMIN_ROLE) {
        if (token == address(0)) revert ZeroAddress();
        if (_tokenSupported[token]) revert TokenAlreadySupported(token);

        _tokenSupported[token] = true;
        _supportedTokens.push(token);

        emit TokenAdded(token, block.timestamp);
    }

    /**
     * @dev Remove a supported token
     * @param token The token address to remove
     */
    function removeToken(address token) external onlyRole(ADMIN_ROLE) {
        if (!_tokenSupported[token]) revert TokenNotSupported(token);

        _tokenSupported[token] = false;

        // Remove from array
        for (uint256 i = 0; i < _supportedTokens.length; i++) {
            if (_supportedTokens[i] == token) {
                _supportedTokens[i] = _supportedTokens[_supportedTokens.length - 1];
                _supportedTokens.pop();
                break;
            }
        }

        emit TokenRemoved(token, block.timestamp);
    }

    /**
     * @dev Deposit funds to the treasury
     * @param token The token address
     * @param amount The amount to deposit
     * @param purpose The purpose of the deposit
     */
    function deposit(
        address token,
        uint256 amount,
        string memory purpose
    ) external nonReentrant whenNotPaused {
        if (amount == 0) revert ZeroAmount();
        if (!_tokenSupported[token]) revert TokenNotSupported(token);

        IERC20(token).safeTransferFrom(_msgSender(), address(this), amount);
        _balances[token] += amount;

        emit FundsDeposited(token, _msgSender(), amount, purpose, block.timestamp);
    }

    /**
     * @dev Spend funds from the treasury
     * @param token The token address
     * @param to The recipient address
     * @param amount The amount to spend
     * @param purpose The purpose of the spending
     */
    function spend(
        address token,
        address to,
        uint256 amount,
        string memory purpose
    ) external nonReentrant whenNotPaused onlyRole(SPENDER_ROLE) {
        if (amount == 0) revert ZeroAmount();
        if (to == address(0)) revert ZeroAddress();
        if (!_tokenSupported[token]) revert TokenNotSupported(token);

        uint256 balance = _balances[token];
        if (balance < amount) {
            revert InsufficientBalance(token, amount, balance);
        }

        // Check spending limits
        _checkSpendingLimit(_msgSender(), token, amount);

        _balances[token] -= amount;
        IERC20(token).safeTransfer(to, amount);

        // Update daily spending
        uint256 currentDay = block.timestamp / 1 days;
        if (_lastSpendDay[_msgSender()][token] != currentDay) {
            _dailySpent[_msgSender()][token] = 0;
            _lastSpendDay[_msgSender()][token] = currentDay;
        }
        _dailySpent[_msgSender()][token] += amount;

        emit FundsSpent(token, to, _msgSender(), amount, purpose, block.timestamp);
    }

    /**
     * @dev Set spending limit for a token
     * @param token The token address
     * @param limit The daily spending limit
     */
    function setSpendingLimit(address token, uint256 limit)
        external
        onlyRole(ADMIN_ROLE)
    {
        uint256 oldLimit = _spendingLimits[token];
        _spendingLimits[token] = limit;

        emit SpendingLimitUpdated(token, oldLimit, limit, block.timestamp);
    }

    /**
     * @dev Emergency withdrawal (admin only)
     * @param token The token address
     * @param to The recipient address
     * @param amount The amount to withdraw
     */
    function emergencyWithdraw(
        address token,
        address to,
        uint256 amount
    ) external nonReentrant onlyRole(ADMIN_ROLE) {
        if (amount == 0) revert ZeroAmount();
        if (to == address(0)) revert ZeroAddress();

        uint256 balance = _balances[token];
        if (balance < amount) {
            revert InsufficientBalance(token, amount, balance);
        }

        _balances[token] -= amount;
        IERC20(token).safeTransfer(to, amount);

        emit FundsSpent(token, to, _msgSender(), amount, "Emergency withdrawal", block.timestamp);
    }

    /**
     * @dev Get balance of a specific token
     * @param token The token address
     * @return The balance amount
     */
    function getBalance(address token) external view returns (uint256) {
        return _balances[token];
    }

    /**
     * @dev Get all supported tokens
     * @return Array of supported token addresses
     */
    function getSupportedTokens() external view returns (address[] memory) {
        return _supportedTokens;
    }

    /**
     * @dev Check if a token is supported
     * @param token The token address
     * @return True if supported
     */
    function isTokenSupported(address token) external view returns (bool) {
        return _tokenSupported[token];
    }

    /**
     * @dev Get spending limit for a token
     * @param token The token address
     * @return The daily spending limit
     */
    function getSpendingLimit(address token) external view returns (uint256) {
        return _spendingLimits[token];
    }

    /**
     * @dev Get daily spent amount for a spender and token
     * @param spender The spender address
     * @param token The token address
     * @return The amount spent today
     */
    function getDailySpent(address spender, address token)
        external
        view
        returns (uint256)
    {
        uint256 currentDay = block.timestamp / 1 days;
        if (_lastSpendDay[spender][token] != currentDay) {
            return 0;
        }
        return _dailySpent[spender][token];
    }

    /**
     * @dev Pause the treasury (admin only)
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause the treasury (admin only)
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Internal function to check spending limits
     * @param spender The spender address
     * @param token The token address
     * @param amount The amount to spend
     */
    function _checkSpendingLimit(
        address spender,
        address token,
        uint256 amount
    ) internal view {
        uint256 limit = _spendingLimits[token];
        if (limit == 0) return; // No limit set

        uint256 currentDay = block.timestamp / 1 days;
        uint256 spentToday = 0;

        if (_lastSpendDay[spender][token] == currentDay) {
            spentToday = _dailySpent[spender][token];
        }

        if (spentToday + amount > limit) {
            revert SpendingLimitExceeded(token, spentToday + amount, limit);
        }
    }

    /**
     * @dev Get total value in treasury (requires price oracle for meaningful calculation)
     * @return tokens Array of token addresses
     * @return balances Array of corresponding balances
     */
    function getTotalValue()
        external
        view
        returns (address[] memory tokens, uint256[] memory balances)
    {
        tokens = _supportedTokens;
        balances = new uint256[](tokens.length);

        for (uint256 i = 0; i < tokens.length; i++) {
            balances[i] = _balances[tokens[i]];
        }
    }
}

contracts/interfaces/IControl.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

/**
 * @title IControl
 * @dev Interface for the Control module - Core DAO infrastructure
 * @author GameDAO AG
 */
interface IControl {
    // Enums
    enum OrgType {
        Individual,
        Company,
        DAO,
        Hybrid
    }

    enum AccessModel {
        Open,        // Anyone can join
        Voting,      // Members vote on new members
        Invite       // Only prime can invite
    }

    enum FeeModel {
        NoFees,      // No membership fees
        Reserve,     // Fees are reserved in member account
        Transfer     // Fees are transferred to treasury
    }

    enum MemberState {
        Inactive,
        Active,
        Pending,     // Application pending approval
        Kicked,
        Banned,
        Exited
    }

    enum OrgState {
        Inactive,
        Active,
        Locked
    }

    // Structs
    struct Organization {
        uint256 index;
        address creator;
        address prime;
        string name;
        string metadataURI;
        OrgType orgType;
        AccessModel accessModel;
        FeeModel feeModel;
        uint256 membershipFee;
        address treasury;
        uint32 memberLimit;
        OrgState state;
        uint256 createdAt;
        uint256 updatedAt;
    }

    struct Member {
        MemberState state;
        uint256 joinedAt;
        uint256 totalContribution;
        bytes32 role;
        uint256 stakedAmount; // $GAME tokens staked
    }

    // Events
    event OrganizationCreated(
        bytes32 indexed orgId,
        address indexed creator,
        address indexed prime,
        string name,
        OrgType orgType,
        uint256 timestamp
    );

    event OrganizationUpdated(
        bytes32 indexed orgId,
        address indexed prime,
        OrgType orgType,
        AccessModel accessModel,
        uint32 memberLimit,
        uint256 timestamp
    );

    event OrganizationStateChanged(
        bytes32 indexed orgId,
        OrgState oldState,
        OrgState newState,
        uint256 timestamp
    );

    event MemberAdded(
        bytes32 indexed orgId,
        address indexed member,
        MemberState state,
        uint256 fee,
        uint256 timestamp
    );

    event MemberStateChanged(
        bytes32 indexed orgId,
        address indexed member,
        MemberState oldState,
        MemberState newState,
        uint256 timestamp
    );

    event MemberRemoved(
        bytes32 indexed orgId,
        address indexed member,
        uint256 timestamp
    );

    event TreasuryFundsSpent(
        bytes32 indexed orgId,
        address indexed beneficiary,
        address indexed token,
        uint256 amount,
        string purpose,
        uint256 timestamp
    );

    event MembershipFeeUpdated(
        bytes32 indexed orgId,
        uint256 oldFee,
        uint256 newFee,
        uint256 timestamp
    );

    // Core Functions
    function createOrganization(
        string memory name,
        string memory metadataURI,
        OrgType orgType,
        AccessModel accessModel,
        FeeModel feeModel,
        uint32 memberLimit,
        uint256 membershipFee,
        uint256 gameStakeRequired
    ) external returns (bytes32 orgId);

    function updateOrganization(
        bytes32 orgId,
        address newPrime,
        OrgType orgType,
        AccessModel accessModel,
        uint32 memberLimit,
        FeeModel feeModel,
        uint256 membershipFee
    ) external;

    function setOrganizationState(bytes32 orgId, OrgState newState) external;

    function addMember(bytes32 orgId, address member) external;

    function updateMemberState(
        bytes32 orgId,
        address member,
        MemberState newState
    ) external;

    function removeMember(bytes32 orgId, address member) external;

    function spendTreasuryFunds(
        bytes32 orgId,
        address token,
        address beneficiary,
        uint256 amount,
        string memory purpose
    ) external;

    // View Functions
    function getOrganization(bytes32 orgId)
        external
        view
        returns (Organization memory);

    function getMember(bytes32 orgId, address member)
        external
        view
        returns (Member memory);

    function isOrganizationActive(bytes32 orgId) external view returns (bool);

    function isMemberActive(bytes32 orgId, address member)
        external
        view
        returns (bool);

    function getOrganizationCount() external view returns (uint256);

    function getMemberCount(bytes32 orgId) external view returns (uint256);

    function getTreasuryAddress(bytes32 orgId) external view returns (address);

    function getGameStakeRequired(bytes32 orgId) external view returns (uint256);

    function hasRole(
        bytes32 orgId,
        address member,
        bytes32 role
    ) external view returns (bool);

    function canJoinOrganization(bytes32 orgId, address member)
        external
        view
        returns (bool);
}

contracts/interfaces/IFlow.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

/**
 * @title IFlow
 * @dev Interface for the Flow module - Crowdfunding and campaign management
 * @author GameDAO AG
 */
interface IFlow {
    // Enums
    enum FlowType {
        Grant,      // Grant funding (no return expected)
        Raise,      // Fundraising with token/equity return
        Lend,       // Lending with interest
        Loan,       // Borrowing funds
        Share,      // Revenue sharing
        Pool        // Liquidity pooling
    }

    enum FlowState {
        Created,    // Campaign created but not started
        Active,     // Campaign is live and accepting contributions
        Paused,     // Campaign temporarily paused
        Succeeded,  // Campaign reached its goal
        Failed,     // Campaign failed to reach minimum
        Locked,     // Campaign locked by admin
        Finalized   // Campaign completed and funds distributed
    }

    enum ContributorState {
        None,       // Not a contributor
        Active,     // Active contributor
        Refunded,   // Received refund
        Rewarded    // Received rewards/tokens
    }

    // Structs
    struct Campaign {
        uint256 index;
        bytes32 organizationId;
        address creator;
        address admin;
        string title;
        string description;
        string metadataURI;
        FlowType flowType;
        FlowState state;
        address paymentToken;
        uint256 target;           // Target amount to raise
        uint256 min;             // Minimum amount to consider success
        uint256 max;             // Maximum amount (cap)
        uint256 raised;          // Current amount raised
        uint256 contributorCount;
        uint256 startTime;
        uint256 endTime;
        uint256 createdAt;
        uint256 updatedAt;
        bool autoFinalize;       // Auto-finalize when target reached
        uint256 protocolFee;     // Protocol fee percentage (basis points)
    }

    struct Contribution {
        address contributor;
        uint256 amount;
        uint256 timestamp;
        ContributorState state;
        string metadata;         // Additional contribution metadata
    }

    struct FlowReward {
        address token;           // Reward token address
        uint256 totalAmount;     // Total reward amount
        uint256 distributedAmount; // Amount already distributed
        mapping(address => uint256) claimed; // Amount claimed by each contributor
    }

    // Events
    event CampaignCreated(
        bytes32 indexed campaignId,
        bytes32 indexed organizationId,
        address indexed creator,
        string title,
        FlowType flowType,
        uint256 target,
        uint256 startTime,
        uint256 endTime,
        uint256 timestamp
    );

    event CampaignUpdated(
        bytes32 indexed campaignId,
        string title,
        string description,
        uint256 target,
        uint256 min,
        uint256 max,
        uint256 endTime,
        uint256 timestamp
    );

    event CampaignStateChanged(
        bytes32 indexed campaignId,
        FlowState oldState,
        FlowState newState,
        uint256 timestamp
    );

    event ContributionMade(
        bytes32 indexed campaignId,
        address indexed contributor,
        uint256 amount,
        uint256 totalRaised,
        uint256 timestamp
    );

    event ContributionRefunded(
        bytes32 indexed campaignId,
        address indexed contributor,
        uint256 amount,
        uint256 timestamp
    );

    event CampaignFinalized(
        bytes32 indexed campaignId,
        FlowState finalState,
        uint256 totalRaised,
        uint256 contributorCount,
        uint256 timestamp
    );

    event RewardsDistributed(
        bytes32 indexed campaignId,
        address indexed token,
        uint256 totalAmount,
        uint256 contributorCount,
        uint256 timestamp
    );

    event ProtocolFeeCollected(
        bytes32 indexed campaignId,
        address indexed token,
        uint256 amount,
        uint256 timestamp
    );

    // Core Functions
    function createCampaign(
        bytes32 organizationId,
        string memory title,
        string memory description,
        string memory metadataURI,
        FlowType flowType,
        address paymentToken,
        uint256 target,
        uint256 min,
        uint256 max,
        uint256 duration,
        bool autoFinalize
    ) external returns (bytes32 campaignId);

    function updateCampaign(
        bytes32 campaignId,
        string memory title,
        string memory description,
        uint256 target,
        uint256 min,
        uint256 max,
        uint256 endTime
    ) external;

    function setCampaignState(bytes32 campaignId, FlowState newState) external;

    function contribute(
        bytes32 campaignId,
        uint256 amount,
        string memory metadata
    ) external;

    function finalizeCampaign(bytes32 campaignId) external;

    function refundContribution(bytes32 campaignId, address contributor) external;

    function distributeRewards(
        bytes32 campaignId,
        address rewardToken,
        uint256 totalRewardAmount
    ) external;

    function claimRewards(bytes32 campaignId, address rewardToken) external;

    function withdrawFunds(
        bytes32 campaignId,
        address to,
        uint256 amount
    ) external;

    // View Functions
    function getCampaign(bytes32 campaignId)
        external
        view
        returns (Campaign memory);

    function getContribution(bytes32 campaignId, address contributor)
        external
        view
        returns (Contribution memory);

    function getCampaignContributors(bytes32 campaignId)
        external
        view
        returns (address[] memory);

    function getCampaignsByOrganization(bytes32 organizationId)
        external
        view
        returns (bytes32[] memory);

    function getCampaignsByState(FlowState state)
        external
        view
        returns (bytes32[] memory);

    function isCampaignActive(bytes32 campaignId) external view returns (bool);

    function canContribute(bytes32 campaignId, address contributor)
        external
        view
        returns (bool);

    function getCampaignProgress(bytes32 campaignId)
        external
        view
        returns (uint256 raised, uint256 target, uint256 percentage);

    function getTimeRemaining(bytes32 campaignId)
        external
        view
        returns (uint256);

    function getCampaignCount() external view returns (uint256);

    function getProtocolFeeRate() external view returns (uint256);

    function calculateProtocolFee(uint256 amount) external view returns (uint256);

    function getRewardDistribution(
        bytes32 campaignId,
        address rewardToken,
        address contributor
    ) external view returns (uint256 totalReward, uint256 claimedAmount, uint256 claimableAmount);
}

contracts/interfaces/IGameDAOModule.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

/**
 * @title IGameDAOModule
 * @dev Interface that all GameDAO modules must implement
 * @author GameDAO AG
 */
interface IGameDAOModule {
    /**
     * @dev Returns the unique identifier for this module
     * @return bytes32 The module identifier
     */
    function moduleId() external pure returns (bytes32);

    /**
     * @dev Returns the version of this module
     * @return string The version string
     */
    function version() external view returns (string memory);

    /**
     * @dev Initializes the module with the registry address
     * @param registry The address of the GameDAO registry
     */
    function initialize(address registry) external;

    /**
     * @dev Called when the module is enabled in the registry
     */
    function onModuleEnabled() external;

    /**
     * @dev Called when the module is disabled in the registry
     */
    function onModuleDisabled() external;

    /**
     * @dev Returns whether the module is initialized
     * @return bool True if initialized
     */
    function isInitialized() external view returns (bool);

    /**
     * @dev Returns the registry address
     * @return address The registry address
     */
    function registry() external view returns (address);
}

contracts/interfaces/IGameDAORegistry.so.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

/**
 * @title IGameDAORegistry
 * @dev Interface for the central GameDAO registry that manages all modules
 * @author GameDAO AG
 */
interface IGameDAORegistry {
    /**
     * @dev Emitted when a module is registered
     * @param moduleId The unique identifier of the module
     * @param moduleAddress The address of the module contract
     * @param version The version of the module
     */
    event ModuleRegistered(bytes32 indexed moduleId, address indexed moduleAddress, string version);

    /**
     * @dev Emitted when a module is enabled
     * @param moduleId The unique identifier of the module
     */
    event ModuleEnabled(bytes32 indexed moduleId);

    /**
     * @dev Emitted when a module is disabled
     * @param moduleId The unique identifier of the module
     */
    event ModuleDisabled(bytes32 indexed moduleId);

    /**
     * @dev Emitted when a module is upgraded
     * @param moduleId The unique identifier of the module
     * @param oldAddress The old module address
     * @param newAddress The new module address
     * @param newVersion The new version
     */
    event ModuleUpgraded(
        bytes32 indexed moduleId,
        address indexed oldAddress,
        address indexed newAddress,
        string newVersion
    );

    /**
     * @dev Registers a new module
     * @param moduleAddress The address of the module contract
     */
    function registerModule(address moduleAddress) external;

    /**
     * @dev Enables a module
     * @param moduleId The unique identifier of the module
     */
    function enableModule(bytes32 moduleId) external;

    /**
     * @dev Disables a module
     * @param moduleId The unique identifier of the module
     */
    function disableModule(bytes32 moduleId) external;

    /**
     * @dev Upgrades a module to a new implementation
     * @param moduleId The unique identifier of the module
     * @param newModuleAddress The address of the new module implementation
     */
    function upgradeModule(bytes32 moduleId, address newModuleAddress) external;

    /**
     * @dev Returns the address of a module
     * @param moduleId The unique identifier of the module
     * @return address The module address
     */
    function getModule(bytes32 moduleId) external view returns (address);

    /**
     * @dev Returns whether a module is enabled
     * @param moduleId The unique identifier of the module
     * @return bool True if the module is enabled
     */
    function isModuleEnabled(bytes32 moduleId) external view returns (bool);

    /**
     * @dev Returns the version of a module
     * @param moduleId The unique identifier of the module
     * @return string The module version
     */
    function getModuleVersion(bytes32 moduleId) external view returns (string memory);

    /**
     * @dev Returns all registered module IDs
     * @return bytes32[] Array of module IDs
     */
    function getAllModules() external view returns (bytes32[] memory);

    /**
     * @dev Returns all enabled module IDs
     * @return bytes32[] Array of enabled module IDs
     */
    function getEnabledModules() external view returns (bytes32[] memory);
}

