contracts/core/GameDAOModule.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "../interfaces/IGameDAOModule.sol";
import "../interfaces/IGameDAORegistry.sol";

/**
 * @title GameDAOModule
 * @dev Base contract for all GameDAO modules
 * @author GameDAO AG
 */
abstract contract GameDAOModule is IGameDAOModule, AccessControl, Pausable, ReentrancyGuard {
    // Role definitions
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");

    // State variables
    address private _registry;
    bool private _initialized;
    string private _version;

    // Events
    event ModuleInitialized(address indexed registry);
    event ModuleEnabled();
    event ModuleDisabled();

    // Errors
    error ModuleAlreadyInitialized();
    error ModuleNotInitialized();
    error OnlyRegistry();
    error InvalidRegistryAddress();

    /**
     * @dev Modifier to restrict access to the registry
     */
    modifier onlyRegistry() {
        if (_msgSender() != _registry) revert OnlyRegistry();
        _;
    }

    /**
     * @dev Modifier to ensure the module is initialized
     */
    modifier onlyInitialized() {
        if (!_initialized) revert ModuleNotInitialized();
        _;
    }

    /**
     * @dev Constructor
     * @param version_ The version of this module
     */
    constructor(string memory version_) {
        _version = version_;

        // Grant admin role to deployer initially
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(ADMIN_ROLE, _msgSender());
    }

    /**
     * @dev Returns the unique identifier for this module
     * @return bytes32 The module identifier
     */
    function moduleId() external pure virtual returns (bytes32);

    /**
     * @dev Returns the version of this module
     * @return string The version string
     */
    function version() external view returns (string memory) {
        return _version;
    }

    /**
     * @dev Initializes the module with the registry address
     * @param registryAddress The address of the GameDAO registry
     */
    function initialize(address registryAddress) external {
        if (_initialized) revert ModuleAlreadyInitialized();
        if (registryAddress == address(0)) revert InvalidRegistryAddress();

        _registry = registryAddress;
        _initialized = true;

        // Grant registry admin permissions
        _grantRole(ADMIN_ROLE, registryAddress);
        _grantRole(OPERATOR_ROLE, registryAddress);

        // Call internal initialization hook
        _onInitialize();

        emit ModuleInitialized(registryAddress);
    }

    /**
     * @dev Called when the module is enabled in the registry
     */
    function onModuleEnabled() external onlyRegistry onlyInitialized {
        _onModuleEnabled();
        emit ModuleEnabled();
    }

    /**
     * @dev Called when the module is disabled in the registry
     */
    function onModuleDisabled() external onlyRegistry onlyInitialized {
        _onModuleDisabled();
        emit ModuleDisabled();
    }

    /**
     * @dev Returns whether the module is initialized
     * @return bool True if initialized
     */
    function isInitialized() external view returns (bool) {
        return _initialized;
    }

    /**
     * @dev Returns the registry address
     * @return address The registry address
     */
    function registry() external view returns (address) {
        return _registry;
    }

    /**
     * @dev Returns the registry contract
     * @return IGameDAORegistry The registry contract
     */
    function getRegistry() internal view onlyInitialized returns (IGameDAORegistry) {
        return IGameDAORegistry(_registry);
    }

    /**
     * @dev Checks if another module is enabled
     * @param targetModuleId The module ID to check
     * @return bool True if the module is enabled
     */
    function isModuleEnabled(bytes32 targetModuleId) internal view onlyInitialized returns (bool) {
        return getRegistry().isModuleEnabled(targetModuleId);
    }

    /**
     * @dev Gets the address of another module
     * @param targetModuleId The module ID to get
     * @return address The module address
     */
    function getModule(bytes32 targetModuleId) internal view onlyInitialized returns (address) {
        return getRegistry().getModule(targetModuleId);
    }

    /**
     * @dev Pauses the module (admin only)
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpauses the module (admin only)
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Internal hook called during initialization
     * Override this in derived contracts to add custom initialization logic
     */
    function _onInitialize() internal virtual {}

    /**
     * @dev Internal hook called when the module is enabled
     * Override this in derived contracts to add custom enable logic
     */
    function _onModuleEnabled() internal virtual {}

    /**
     * @dev Internal hook called when the module is disabled
     * Override this in derived contracts to add custom disable logic
     */
    function _onModuleDisabled() internal virtual {}

    /**
     * @dev Internal function to update the module version
     * @param newVersion The new version string
     */
    function _updateVersion(string memory newVersion) internal {
        _version = newVersion;
    }

    /**
     * @dev Emergency pause function that can be called by the registry
     */
    function emergencyPause() external onlyRegistry {
        _pause();
    }

    /**
     * @dev Emergency unpause function that can be called by the registry
     */
    function emergencyUnpause() external onlyRegistry {
        _unpause();
    }

    /**
     * @dev Override to ensure only initialized modules can be paused
     */
    function _pause() internal virtual override onlyInitialized {
        super._pause();
    }

    /**
     * @dev Override to ensure only initialized modules can be unpaused
     */
    function _unpause() internal virtual override onlyInitialized {
        super._unpause();
    }
}

contracts/interfaces/IControl.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

/**
 * @title IControl
 * @dev Interface for the Control module - Core DAO infrastructure
 * @author GameDAO AG
 */
interface IControl {
    // Enums
    enum OrgType {
        Individual,
        Company,
        DAO,
        Hybrid
    }

    enum AccessModel {
        Open,        // Anyone can join
        Voting,      // Members vote on new members
        Invite       // Only prime can invite
    }

    enum FeeModel {
        NoFees,      // No membership fees
        Reserve,     // Fees are reserved in member account
        Transfer     // Fees are transferred to treasury
    }

    enum MemberState {
        Inactive,
        Active,
        Pending,     // Application pending approval
        Kicked,
        Banned,
        Exited
    }

    enum OrgState {
        Inactive,
        Active,
        Locked
    }

    // Structs
    struct Organization {
        uint256 index;
        address creator;
        address prime;
        string name;
        string metadataURI;
        OrgType orgType;
        AccessModel accessModel;
        FeeModel feeModel;
        uint256 membershipFee;
        address treasury;
        uint32 memberLimit;
        OrgState state;
        uint256 createdAt;
        uint256 updatedAt;
    }

    struct Member {
        MemberState state;
        uint256 joinedAt;
        uint256 totalContribution;
        bytes32 role;
        uint256 stakedAmount; // $GAME tokens staked
    }

    // Events
    event OrganizationCreated(
        bytes32 indexed orgId,
        address indexed creator,
        address indexed prime,
        string name,
        OrgType orgType,
        uint256 timestamp
    );

    event OrganizationUpdated(
        bytes32 indexed orgId,
        address indexed prime,
        OrgType orgType,
        AccessModel accessModel,
        uint32 memberLimit,
        uint256 timestamp
    );

    event OrganizationStateChanged(
        bytes32 indexed orgId,
        OrgState oldState,
        OrgState newState,
        uint256 timestamp
    );

    event MemberAdded(
        bytes32 indexed orgId,
        address indexed member,
        MemberState state,
        uint256 fee,
        uint256 timestamp
    );

    event MemberStateChanged(
        bytes32 indexed orgId,
        address indexed member,
        MemberState oldState,
        MemberState newState,
        uint256 timestamp
    );

    event MemberRemoved(
        bytes32 indexed orgId,
        address indexed member,
        uint256 timestamp
    );

    event TreasuryFundsSpent(
        bytes32 indexed orgId,
        address indexed beneficiary,
        address indexed token,
        uint256 amount,
        string purpose,
        uint256 timestamp
    );

    event MembershipFeeUpdated(
        bytes32 indexed orgId,
        uint256 oldFee,
        uint256 newFee,
        uint256 timestamp
    );

    // Core Functions
    function createOrganization(
        string memory name,
        string memory metadataURI,
        OrgType orgType,
        AccessModel accessModel,
        FeeModel feeModel,
        uint32 memberLimit,
        uint256 membershipFee,
        uint256 gameStakeRequired
    ) external returns (bytes32 orgId);

    function updateOrganization(
        bytes32 orgId,
        address newPrime,
        OrgType orgType,
        AccessModel accessModel,
        uint32 memberLimit,
        FeeModel feeModel,
        uint256 membershipFee
    ) external;

    function setOrganizationState(bytes32 orgId, OrgState newState) external;

    function addMember(bytes32 orgId, address member) external;

    function updateMemberState(
        bytes32 orgId,
        address member,
        MemberState newState
    ) external;

    function removeMember(bytes32 orgId, address member) external;

    function spendTreasuryFunds(
        bytes32 orgId,
        address token,
        address beneficiary,
        uint256 amount,
        string memory purpose
    ) external;

    // View Functions
    function getOrganization(bytes32 orgId)
        external
        view
        returns (Organization memory);

    function getMember(bytes32 orgId, address member)
        external
        view
        returns (Member memory);

    function isOrganizationActive(bytes32 orgId) external view returns (bool);

    function isMemberActive(bytes32 orgId, address member)
        external
        view
        returns (bool);

    function getOrganizationCount() external view returns (uint256);

    function getMemberCount(bytes32 orgId) external view returns (uint256);

    function getTreasuryAddress(bytes32 orgId) external view returns (address);

    function getGameStakeRequired(bytes32 orgId) external view returns (uint256);

    function hasRole(
        bytes32 orgId,
        address member,
        bytes32 role
    ) external view returns (bool);

    function canJoinOrganization(bytes32 orgId, address member)
        external
        view
        returns (bool);
}

contracts/interfaces/IFlow.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

/**
 * @title IFlow
 * @dev Interface for the Flow module - Crowdfunding and campaign management
 * @author GameDAO AG
 */
interface IFlow {
    // Enums
    enum FlowType {
        Grant,      // Grant funding (no return expected)
        Raise,      // Fundraising with token/equity return
        Lend,       // Lending with interest
        Loan,       // Borrowing funds
        Share,      // Revenue sharing
        Pool        // Liquidity pooling
    }

    enum FlowState {
        Created,    // Campaign created but not started
        Active,     // Campaign is live and accepting contributions
        Paused,     // Campaign temporarily paused
        Succeeded,  // Campaign reached its goal
        Failed,     // Campaign failed to reach minimum
        Locked,     // Campaign locked by admin
        Finalized   // Campaign completed and funds distributed
    }

    enum ContributorState {
        None,       // Not a contributor
        Active,     // Active contributor
        Refunded,   // Received refund
        Rewarded    // Received rewards/tokens
    }

    // Structs
    struct Campaign {
        uint256 index;
        bytes32 organizationId;
        address creator;
        address admin;
        string title;
        string description;
        string metadataURI;
        FlowType flowType;
        FlowState state;
        address paymentToken;
        uint256 target;           // Target amount to raise
        uint256 min;             // Minimum amount to consider success
        uint256 max;             // Maximum amount (cap)
        uint256 raised;          // Current amount raised
        uint256 contributorCount;
        uint256 startTime;
        uint256 endTime;
        uint256 createdAt;
        uint256 updatedAt;
        bool autoFinalize;       // Auto-finalize when target reached
        uint256 protocolFee;     // Protocol fee percentage (basis points)
    }

    struct Contribution {
        address contributor;
        uint256 amount;
        uint256 timestamp;
        ContributorState state;
        string metadata;         // Additional contribution metadata
    }

    struct FlowReward {
        address token;           // Reward token address
        uint256 totalAmount;     // Total reward amount
        uint256 distributedAmount; // Amount already distributed
        mapping(address => uint256) claimed; // Amount claimed by each contributor
    }

    // Events
    event CampaignCreated(
        bytes32 indexed campaignId,
        bytes32 indexed organizationId,
        address indexed creator,
        string title,
        FlowType flowType,
        uint256 target,
        uint256 startTime,
        uint256 endTime,
        uint256 timestamp
    );

    event CampaignUpdated(
        bytes32 indexed campaignId,
        string title,
        string description,
        uint256 target,
        uint256 min,
        uint256 max,
        uint256 endTime,
        uint256 timestamp
    );

    event CampaignStateChanged(
        bytes32 indexed campaignId,
        FlowState oldState,
        FlowState newState,
        uint256 timestamp
    );

    event ContributionMade(
        bytes32 indexed campaignId,
        address indexed contributor,
        uint256 amount,
        uint256 totalRaised,
        uint256 timestamp
    );

    event ContributionRefunded(
        bytes32 indexed campaignId,
        address indexed contributor,
        uint256 amount,
        uint256 timestamp
    );

    event CampaignFinalized(
        bytes32 indexed campaignId,
        FlowState finalState,
        uint256 totalRaised,
        uint256 contributorCount,
        uint256 timestamp
    );

    event RewardsDistributed(
        bytes32 indexed campaignId,
        address indexed token,
        uint256 totalAmount,
        uint256 contributorCount,
        uint256 timestamp
    );

    event ProtocolFeeCollected(
        bytes32 indexed campaignId,
        address indexed token,
        uint256 amount,
        uint256 timestamp
    );

    // Core Functions
    function createCampaign(
        bytes32 organizationId,
        string memory title,
        string memory description,
        string memory metadataURI,
        FlowType flowType,
        address paymentToken,
        uint256 target,
        uint256 min,
        uint256 max,
        uint256 duration,
        bool autoFinalize
    ) external returns (bytes32 campaignId);

    function updateCampaign(
        bytes32 campaignId,
        string memory title,
        string memory description,
        uint256 target,
        uint256 min,
        uint256 max,
        uint256 endTime
    ) external;

    function setCampaignState(bytes32 campaignId, FlowState newState) external;

    function contribute(
        bytes32 campaignId,
        uint256 amount,
        string memory metadata
    ) external payable;

    function finalizeCampaign(bytes32 campaignId) external;

    function refundContribution(bytes32 campaignId, address contributor) external;

    function distributeRewards(
        bytes32 campaignId,
        address rewardToken,
        uint256 totalRewardAmount
    ) external;

    function claimRewards(bytes32 campaignId, address rewardToken) external;

    function withdrawFunds(
        bytes32 campaignId,
        address to,
        uint256 amount
    ) external;

    // View Functions
    function getCampaign(bytes32 campaignId)
        external
        view
        returns (Campaign memory);

    function getContribution(bytes32 campaignId, address contributor)
        external
        view
        returns (Contribution memory);

    function getCampaignContributors(bytes32 campaignId)
        external
        view
        returns (address[] memory);

    function getCampaignsByOrganization(bytes32 organizationId)
        external
        view
        returns (bytes32[] memory);

    function getCampaignsByState(FlowState state)
        external
        view
        returns (bytes32[] memory);

    function isCampaignActive(bytes32 campaignId) external view returns (bool);

    function canContribute(bytes32 campaignId, address contributor)
        external
        view
        returns (bool);

    function getCampaignProgress(bytes32 campaignId)
        external
        view
        returns (uint256 raised, uint256 target, uint256 percentage);

    function getTimeRemaining(bytes32 campaignId)
        external
        view
        returns (uint256);

    function getCampaignCount() external view returns (uint256);

    function getProtocolFeeRate() external view returns (uint256);

    function calculateProtocolFee(uint256 amount) external view returns (uint256);

    function getRewardDistribution(
        bytes32 campaignId,
        address rewardToken,
        address contributor
    ) external view returns (uint256 totalReward, uint256 claimedAmount, uint256 claimableAmount);
}

contracts/interfaces/IGameDAOModule.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

/**
 * @title IGameDAOModule
 * @dev Interface that all GameDAO modules must implement
 * @author GameDAO AG
 */
interface IGameDAOModule {
    /**
     * @dev Returns the unique identifier for this module
     * @return bytes32 The module identifier
     */
    function moduleId() external pure returns (bytes32);

    /**
     * @dev Returns the version of this module
     * @return string The version string
     */
    function version() external view returns (string memory);

    /**
     * @dev Initializes the module with the registry address
     * @param registry The address of the GameDAO registry
     */
    function initialize(address registry) external;

    /**
     * @dev Called when the module is enabled in the registry
     */
    function onModuleEnabled() external;

    /**
     * @dev Called when the module is disabled in the registry
     */
    function onModuleDisabled() external;

    /**
     * @dev Returns whether the module is initialized
     * @return bool True if initialized
     */
    function isInitialized() external view returns (bool);

    /**
     * @dev Returns the registry address
     * @return address The registry address
     */
    function registry() external view returns (address);
}

contracts/interfaces/IGameDAORegistry.so.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

/**
 * @title IGameDAORegistry
 * @dev Interface for the central GameDAO registry that manages all modules
 * @author GameDAO AG
 */
interface IGameDAORegistry {
    /**
     * @dev Emitted when a module is registered
     * @param moduleId The unique identifier of the module
     * @param moduleAddress The address of the module contract
     * @param version The version of the module
     */
    event ModuleRegistered(bytes32 indexed moduleId, address indexed moduleAddress, string version);

    /**
     * @dev Emitted when a module is enabled
     * @param moduleId The unique identifier of the module
     */
    event ModuleEnabled(bytes32 indexed moduleId);

    /**
     * @dev Emitted when a module is disabled
     * @param moduleId The unique identifier of the module
     */
    event ModuleDisabled(bytes32 indexed moduleId);

    /**
     * @dev Emitted when a module is upgraded
     * @param moduleId The unique identifier of the module
     * @param oldAddress The old module address
     * @param newAddress The new module address
     * @param newVersion The new version
     */
    event ModuleUpgraded(
        bytes32 indexed moduleId,
        address indexed oldAddress,
        address indexed newAddress,
        string newVersion
    );

    /**
     * @dev Registers a new module
     * @param moduleAddress The address of the module contract
     */
    function registerModule(address moduleAddress) external;

    /**
     * @dev Enables a module
     * @param moduleId The unique identifier of the module
     */
    function enableModule(bytes32 moduleId) external;

    /**
     * @dev Disables a module
     * @param moduleId The unique identifier of the module
     */
    function disableModule(bytes32 moduleId) external;

    /**
     * @dev Upgrades a module to a new implementation
     * @param moduleId The unique identifier of the module
     * @param newModuleAddress The address of the new module implementation
     */
    function upgradeModule(bytes32 moduleId, address newModuleAddress) external;

    /**
     * @dev Returns the address of a module
     * @param moduleId The unique identifier of the module
     * @return address The module address
     */
    function getModule(bytes32 moduleId) external view returns (address);

    /**
     * @dev Returns whether a module is enabled
     * @param moduleId The unique identifier of the module
     * @return bool True if the module is enabled
     */
    function isModuleEnabled(bytes32 moduleId) external view returns (bool);

    /**
     * @dev Returns the version of a module
     * @param moduleId The unique identifier of the module
     * @return string The module version
     */
    function getModuleVersion(bytes32 moduleId) external view returns (string memory);

    /**
     * @dev Returns all registered module IDs
     * @return bytes32[] Array of module IDs
     */
    function getAllModules() external view returns (bytes32[] memory);

    /**
     * @dev Returns all enabled module IDs
     * @return bytes32[] Array of enabled module IDs
     */
    function getEnabledModules() external view returns (bytes32[] memory);
}

contracts/modules/Flow/Flow.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

import "../../core/GameDAOModule.sol";
import "../../interfaces/IFlow.sol";
import "../../interfaces/IControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
/**
 * @title Flow
 * @dev Implementation of the Flow module for campaign management and crowdfunding
 * @author GameDAO AG
 */
contract Flow is GameDAOModule, IFlow {
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using EnumerableSet for EnumerableSet.AddressSet;

    // Constants
    bytes32 public constant FLOW_ADMIN_ROLE = keccak256("FLOW_ADMIN_ROLE");
    bytes32 public constant CAMPAIGN_CREATOR_ROLE = keccak256("CAMPAIGN_CREATOR_ROLE");
    uint256 public constant MAX_PROTOCOL_FEE = 1000; // 10% max protocol fee
    uint256 public constant BASIS_POINTS = 10000;

    // State variables
    mapping(bytes32 => Campaign) private _campaigns;
    mapping(bytes32 => mapping(address => Contribution)) private _contributions;
    mapping(bytes32 => EnumerableSet.AddressSet) private _campaignContributors;
    mapping(bytes32 => mapping(address => FlowReward)) private _rewards;
    mapping(bytes32 => EnumerableSet.Bytes32Set) private _organizationCampaigns;
    mapping(FlowState => EnumerableSet.Bytes32Set) private _campaignsByState;

    EnumerableSet.Bytes32Set private _allCampaigns;
    uint256 private _campaignCounter;
    uint256 private _protocolFeeRate; // In basis points (100 = 1%)
    address private _protocolFeeRecipient;

    // Errors
    error CampaignNotFound(bytes32 campaignId);
    error CampaignNotActive(bytes32 campaignId);
    error CampaignEnded(bytes32 campaignId);
    error CampaignNotEnded(bytes32 campaignId);
    error InvalidCampaignParameters();
    error InvalidContributionAmount(uint256 amount);
    error ContributionExceedsMax(uint256 amount, uint256 max);
    error CampaignCapReached(uint256 raised, uint256 max);
    error UnauthorizedCampaignAccess(bytes32 campaignId, address caller);
    error InvalidProtocolFee(uint256 fee);
    error NoContributionFound(bytes32 campaignId, address contributor);
    error ContributionAlreadyRefunded(bytes32 campaignId, address contributor);
    error RewardsAlreadyClaimed(bytes32 campaignId, address contributor, address token);
    error InsufficientRewardBalance(bytes32 campaignId, address token);
    error OrganizationNotFound(bytes32 organizationId);

    /**
     * @dev Constructor
     */
    constructor() GameDAOModule("1.0.0") {
        _protocolFeeRate = 250; // 2.5% default protocol fee
        _protocolFeeRecipient = _msgSender();

        // Grant roles to deployer initially
        _grantRole(FLOW_ADMIN_ROLE, _msgSender());
        _grantRole(CAMPAIGN_CREATOR_ROLE, _msgSender());
    }

    /**
     * @dev Returns the unique identifier for this module
     */
    function moduleId() external pure override returns (bytes32) {
        return keccak256("FLOW");
    }

    /**
     * @dev Internal initialization hook
     */
    function _onInitialize() internal override {
        // Grant admin roles to the registry - access via this.registry() for external call
        address registryAddr = this.registry();
        _grantRole(FLOW_ADMIN_ROLE, registryAddr);
        _grantRole(CAMPAIGN_CREATOR_ROLE, registryAddr);
    }

    /**
     * @dev Create a new campaign
     */
    function createCampaign(
        bytes32 organizationId,
        string memory title,
        string memory description,
        string memory metadataURI,
        FlowType flowType,
        address paymentToken,
        uint256 target,
        uint256 min,
        uint256 max,
        uint256 duration,
        bool autoFinalize
    ) external override onlyInitialized whenNotPaused nonReentrant returns (bytes32 campaignId) {
        // Validate organization exists through Control module
        _validateOrganization(organizationId);

        // Validate campaign parameters
        if (bytes(title).length == 0) revert InvalidCampaignParameters();
        if (target == 0 || min > target || (max > 0 && max < target)) {
            revert InvalidCampaignParameters();
        }
        if (duration == 0) revert InvalidCampaignParameters();

        // Generate unique campaign ID
        campaignId = keccak256(abi.encodePacked(
            organizationId,
            _msgSender(),
            title,
            block.timestamp,
            _campaignCounter++
        ));

        // Create campaign
        Campaign storage campaign = _campaigns[campaignId];
        campaign.index = _campaignCounter;
        campaign.organizationId = organizationId;
        campaign.creator = _msgSender();
        campaign.admin = _msgSender();
        campaign.title = title;
        campaign.description = description;
        campaign.metadataURI = metadataURI;
        campaign.flowType = flowType;
        campaign.state = FlowState.Created;
        campaign.paymentToken = paymentToken;
        campaign.target = target;
        campaign.min = min;
        campaign.max = max;
        campaign.raised = 0;
        campaign.contributorCount = 0;
        campaign.startTime = block.timestamp;
        campaign.endTime = block.timestamp + duration;
        campaign.createdAt = block.timestamp;
        campaign.updatedAt = block.timestamp;
        campaign.autoFinalize = autoFinalize;
        campaign.protocolFee = _protocolFeeRate;

        // Add to tracking sets
        _allCampaigns.add(campaignId);
        _organizationCampaigns[organizationId].add(campaignId);
        _campaignsByState[FlowState.Created].add(campaignId);

        emit CampaignCreated(
            campaignId,
            organizationId,
            _msgSender(),
            title,
            flowType,
            target,
            campaign.startTime,
            campaign.endTime,
            block.timestamp
        );

        return campaignId;
    }

    /**
     * @dev Update an existing campaign
     */
    function updateCampaign(
        bytes32 campaignId,
        string memory title,
        string memory description,
        uint256 target,
        uint256 min,
        uint256 max,
        uint256 endTime
    ) external override onlyInitialized whenNotPaused {
        Campaign storage campaign = _campaigns[campaignId];
        if (campaign.creator == address(0)) revert CampaignNotFound(campaignId);

        // Only creator or admin can update
        if (_msgSender() != campaign.creator &&
            _msgSender() != campaign.admin &&
            !hasRole(FLOW_ADMIN_ROLE, _msgSender())) {
            revert UnauthorizedCampaignAccess(campaignId, _msgSender());
        }

        // Cannot update if campaign is finalized
        if (campaign.state == FlowState.Finalized) {
            revert UnauthorizedCampaignAccess(campaignId, _msgSender());
        }

        // Validate new parameters
        if (bytes(title).length > 0) campaign.title = title;
        if (bytes(description).length > 0) campaign.description = description;
        if (target > 0 && min <= target && (max == 0 || max >= target)) {
            campaign.target = target;
            campaign.min = min;
            campaign.max = max;
        }
        if (endTime > block.timestamp && endTime > campaign.startTime) {
            campaign.endTime = endTime;
        }

        campaign.updatedAt = block.timestamp;

        emit CampaignUpdated(
            campaignId,
            campaign.title,
            campaign.description,
            campaign.target,
            campaign.min,
            campaign.max,
            campaign.endTime,
            block.timestamp
        );
    }

    /**
     * @dev Set campaign state (admin only)
     */
    function setCampaignState(bytes32 campaignId, FlowState newState)
        external
        override
        onlyRole(FLOW_ADMIN_ROLE)
        onlyInitialized
        whenNotPaused
    {
        Campaign storage campaign = _campaigns[campaignId];
        if (campaign.creator == address(0)) revert CampaignNotFound(campaignId);

        FlowState oldState = campaign.state;
        if (oldState == newState) return;

        // Remove from old state tracking
        _campaignsByState[oldState].remove(campaignId);

        // Update state
        campaign.state = newState;
        campaign.updatedAt = block.timestamp;

        // Add to new state tracking
        _campaignsByState[newState].add(campaignId);

        emit CampaignStateChanged(campaignId, oldState, newState, block.timestamp);
    }

    /**
     * @dev Make a contribution to a campaign
     */
    function contribute(
        bytes32 campaignId,
        uint256 amount,
        string memory metadata
    ) external payable override onlyInitialized whenNotPaused nonReentrant {
        Campaign storage campaign = _campaigns[campaignId];
        if (campaign.creator == address(0)) revert CampaignNotFound(campaignId);

        // Check campaign state and timing
        if (campaign.state != FlowState.Active && campaign.state != FlowState.Created) {
            revert CampaignNotActive(campaignId);
        }
        if (block.timestamp >= campaign.endTime) {
            revert CampaignEnded(campaignId);
        }
        if (amount == 0) revert InvalidContributionAmount(amount);

        // Check contribution limits
        if (campaign.max > 0 && campaign.raised + amount > campaign.max) {
            revert ContributionExceedsMax(amount, campaign.max - campaign.raised);
        }

        // Transfer tokens from contributor
        if (campaign.paymentToken == address(0)) {
            // ETH contribution
            if (msg.value != amount) revert InvalidContributionAmount(amount);
        } else {
            // ERC20 contribution
            IERC20(campaign.paymentToken).safeTransferFrom(_msgSender(), address(this), amount);
        }

        // Update contribution tracking
        Contribution storage contribution = _contributions[campaignId][_msgSender()];
        bool isNewContributor = contribution.contributor == address(0);

        if (isNewContributor) {
            contribution.contributor = _msgSender();
            contribution.state = ContributorState.Active;
            _campaignContributors[campaignId].add(_msgSender());
            campaign.contributorCount++;
        }

        contribution.amount += amount;
        contribution.timestamp = block.timestamp;
        contribution.metadata = metadata;

        // Update campaign totals
        campaign.raised += amount;
        campaign.updatedAt = block.timestamp;

        // Auto-activate campaign if it was just created
        if (campaign.state == FlowState.Created) {
            _campaignsByState[FlowState.Created].remove(campaignId);
            campaign.state = FlowState.Active;
            _campaignsByState[FlowState.Active].add(campaignId);

            emit CampaignStateChanged(campaignId, FlowState.Created, FlowState.Active, block.timestamp);
        }

        emit ContributionMade(campaignId, _msgSender(), amount, campaign.raised, block.timestamp);

        // Auto-finalize if target reached and auto-finalize enabled
        if (campaign.autoFinalize && campaign.raised >= campaign.target) {
            _finalizeCampaignInternal(campaignId);
        }
    }

    /**
     * @dev Finalize a campaign
     */
    function finalizeCampaign(bytes32 campaignId)
        external
        override
        onlyInitialized
        whenNotPaused
        nonReentrant
    {
        Campaign storage campaign = _campaigns[campaignId];
        if (campaign.creator == address(0)) revert CampaignNotFound(campaignId);

        // Only creator, admin, or protocol admin can finalize
        if (_msgSender() != campaign.creator &&
            _msgSender() != campaign.admin &&
            !hasRole(FLOW_ADMIN_ROLE, _msgSender())) {
            revert UnauthorizedCampaignAccess(campaignId, _msgSender());
        }

        _finalizeCampaignInternal(campaignId);
    }

    /**
     * @dev Internal function to finalize a campaign
     */
    function _finalizeCampaignInternal(bytes32 campaignId) internal {
        Campaign storage campaign = _campaigns[campaignId];

        // Determine final state
        FlowState finalState;
        if (campaign.raised >= campaign.min) {
            finalState = FlowState.Succeeded;
        } else {
            finalState = FlowState.Failed;
        }

        // Update state tracking
        _campaignsByState[campaign.state].remove(campaignId);
        campaign.state = finalState;
        campaign.updatedAt = block.timestamp;
        _campaignsByState[finalState].add(campaignId);

        // Collect protocol fee if campaign succeeded
        if (finalState == FlowState.Succeeded && campaign.raised > 0) {
            uint256 protocolFee = calculateProtocolFee(campaign.raised);
            if (protocolFee > 0) {
                _transferFunds(campaign.paymentToken, _protocolFeeRecipient, protocolFee);

                emit ProtocolFeeCollected(
                    campaignId,
                    campaign.paymentToken,
                    protocolFee,
                    block.timestamp
                );
            }
        }

        emit CampaignFinalized(
            campaignId,
            finalState,
            campaign.raised,
            campaign.contributorCount,
            block.timestamp
        );

        // Move to finalized state
        _campaignsByState[finalState].remove(campaignId);
        campaign.state = FlowState.Finalized;
        _campaignsByState[FlowState.Finalized].add(campaignId);
    }

    /**
     * @dev Refund a contribution (for failed campaigns)
     */
    function refundContribution(bytes32 campaignId, address contributor)
        external
        override
        onlyInitialized
        whenNotPaused
        nonReentrant
    {
        Campaign storage campaign = _campaigns[campaignId];
        if (campaign.creator == address(0)) revert CampaignNotFound(campaignId);

        // Only allow refunds for failed campaigns
        if (campaign.state != FlowState.Failed && campaign.state != FlowState.Finalized) {
            revert CampaignNotActive(campaignId);
        }

        Contribution storage contribution = _contributions[campaignId][contributor];
        if (contribution.contributor == address(0)) {
            revert NoContributionFound(campaignId, contributor);
        }
        if (contribution.state == ContributorState.Refunded) {
            revert ContributionAlreadyRefunded(campaignId, contributor);
        }

        uint256 refundAmount = contribution.amount;
        contribution.state = ContributorState.Refunded;
        contribution.amount = 0;

        // Transfer refund
        _transferFunds(campaign.paymentToken, contributor, refundAmount);

        emit ContributionRefunded(campaignId, contributor, refundAmount, block.timestamp);
    }

    /**
     * @dev Distribute rewards to contributors
     */
    function distributeRewards(
        bytes32 campaignId,
        address rewardToken,
        uint256 totalRewardAmount
    ) external override onlyInitialized whenNotPaused nonReentrant {
        Campaign storage campaign = _campaigns[campaignId];
        if (campaign.creator == address(0)) revert CampaignNotFound(campaignId);

        // Only creator or admin can distribute rewards
        if (_msgSender() != campaign.creator &&
            _msgSender() != campaign.admin &&
            !hasRole(FLOW_ADMIN_ROLE, _msgSender())) {
            revert UnauthorizedCampaignAccess(campaignId, _msgSender());
        }

        // Campaign must be successful
        if (campaign.state != FlowState.Succeeded && campaign.state != FlowState.Finalized) {
            revert CampaignNotActive(campaignId);
        }

        // Transfer reward tokens to contract
        IERC20(rewardToken).safeTransferFrom(_msgSender(), address(this), totalRewardAmount);

        // Set up reward distribution
        FlowReward storage reward = _rewards[campaignId][rewardToken];
        reward.token = rewardToken;
        reward.totalAmount = totalRewardAmount;
        reward.distributedAmount = 0;

        emit RewardsDistributed(
            campaignId,
            rewardToken,
            totalRewardAmount,
            campaign.contributorCount,
            block.timestamp
        );
    }

    /**
     * @dev Claim rewards for a contributor
     */
    function claimRewards(bytes32 campaignId, address rewardToken)
        external
        override
        onlyInitialized
        whenNotPaused
        nonReentrant
    {
        Campaign storage campaign = _campaigns[campaignId];
        if (campaign.creator == address(0)) revert CampaignNotFound(campaignId);

        Contribution storage contribution = _contributions[campaignId][_msgSender()];
        if (contribution.contributor == address(0)) {
            revert NoContributionFound(campaignId, _msgSender());
        }

        FlowReward storage reward = _rewards[campaignId][rewardToken];
        if (reward.token == address(0)) revert InsufficientRewardBalance(campaignId, rewardToken);

        // Check if already claimed
        if (reward.claimed[_msgSender()] > 0) {
            revert RewardsAlreadyClaimed(campaignId, _msgSender(), rewardToken);
        }

        // Calculate proportional reward
        uint256 rewardAmount = (contribution.amount * reward.totalAmount) / campaign.raised;

        reward.claimed[_msgSender()] = rewardAmount;
        reward.distributedAmount += rewardAmount;
        contribution.state = ContributorState.Rewarded;

        // Transfer reward
        IERC20(rewardToken).safeTransfer(_msgSender(), rewardAmount);
    }

    /**
     * @dev Withdraw funds from a successful campaign
     */
    function withdrawFunds(
        bytes32 campaignId,
        address to,
        uint256 amount
    ) external override onlyInitialized whenNotPaused nonReentrant {
        Campaign storage campaign = _campaigns[campaignId];
        if (campaign.creator == address(0)) revert CampaignNotFound(campaignId);

        // Only creator or admin can withdraw
        if (_msgSender() != campaign.creator &&
            _msgSender() != campaign.admin &&
            !hasRole(FLOW_ADMIN_ROLE, _msgSender())) {
            revert UnauthorizedCampaignAccess(campaignId, _msgSender());
        }

        // Campaign must be successful
        if (campaign.state != FlowState.Succeeded && campaign.state != FlowState.Finalized) {
            revert CampaignNotActive(campaignId);
        }

        // Calculate available funds (after protocol fee)
        uint256 protocolFee = calculateProtocolFee(campaign.raised);
        uint256 availableFunds = campaign.raised - protocolFee;

        if (amount > availableFunds) {
            revert InvalidContributionAmount(amount);
        }

        _transferFunds(campaign.paymentToken, to, amount);
    }

    /**
     * @dev Internal function to transfer funds
     */
    function _transferFunds(address token, address to, uint256 amount) internal {
        if (token == address(0)) {
            // ETH transfer
            (bool success, ) = payable(to).call{value: amount}("");
            require(success, "ETH transfer failed");
        } else {
            // ERC20 transfer
            IERC20(token).safeTransfer(to, amount);
        }
    }

    /**
     * @dev Validate organization exists through Control module
     */
    function _validateOrganization(bytes32 organizationId) internal view {
        address controlModule = getModule(keccak256("CONTROL"));
        if (controlModule == address(0)) revert OrganizationNotFound(organizationId);

        IControl control = IControl(controlModule);
        if (!control.isOrganizationActive(organizationId)) {
            revert OrganizationNotFound(organizationId);
        }
    }

    // View Functions
    function getCampaign(bytes32 campaignId) external view override returns (Campaign memory) {
        return _campaigns[campaignId];
    }

    function getContribution(bytes32 campaignId, address contributor)
        external
        view
        override
        returns (Contribution memory)
    {
        return _contributions[campaignId][contributor];
    }

    function getCampaignContributors(bytes32 campaignId)
        external
        view
        override
        returns (address[] memory)
    {
        return _campaignContributors[campaignId].values();
    }

    function getCampaignsByOrganization(bytes32 organizationId)
        external
        view
        override
        returns (bytes32[] memory)
    {
        return _organizationCampaigns[organizationId].values();
    }

    function getCampaignsByState(FlowState state)
        external
        view
        override
        returns (bytes32[] memory)
    {
        return _campaignsByState[state].values();
    }

    function isCampaignActive(bytes32 campaignId) external view override returns (bool) {
        Campaign storage campaign = _campaigns[campaignId];
        return campaign.state == FlowState.Active && block.timestamp < campaign.endTime;
    }

        function canContribute(bytes32 campaignId, address /* contributor */)
        external
        view
        override
        returns (bool)
    {
        Campaign storage campaign = _campaigns[campaignId];
        return campaign.creator != address(0) &&
               (campaign.state == FlowState.Active || campaign.state == FlowState.Created) &&
               block.timestamp < campaign.endTime &&
               (campaign.max == 0 || campaign.raised < campaign.max);
    }

    function getCampaignProgress(bytes32 campaignId)
        external
        view
        override
        returns (uint256 raised, uint256 target, uint256 percentage)
    {
        Campaign storage campaign = _campaigns[campaignId];
        raised = campaign.raised;
        target = campaign.target;
        percentage = target > 0 ? (raised * 100) / target : 0;
    }

    function getTimeRemaining(bytes32 campaignId) external view override returns (uint256) {
        Campaign storage campaign = _campaigns[campaignId];
        if (block.timestamp >= campaign.endTime) return 0;
        return campaign.endTime - block.timestamp;
    }

    function getCampaignCount() external view override returns (uint256) {
        return _allCampaigns.length();
    }

    function getProtocolFeeRate() external view override returns (uint256) {
        return _protocolFeeRate;
    }

    function calculateProtocolFee(uint256 amount) public view override returns (uint256) {
        return (amount * _protocolFeeRate) / BASIS_POINTS;
    }

    function getRewardDistribution(
        bytes32 campaignId,
        address rewardToken,
        address contributor
    ) external view override returns (uint256 totalReward, uint256 claimedAmount, uint256 claimableAmount) {
        Campaign storage campaign = _campaigns[campaignId];
        Contribution storage contribution = _contributions[campaignId][contributor];
        FlowReward storage reward = _rewards[campaignId][rewardToken];

        if (contribution.contributor == address(0) || reward.token == address(0)) {
            return (0, 0, 0);
        }

        totalReward = (contribution.amount * reward.totalAmount) / campaign.raised;
        claimedAmount = reward.claimed[contributor];
        claimableAmount = totalReward - claimedAmount;
    }

    // Admin Functions
    function setProtocolFeeRate(uint256 newRate) external onlyRole(FLOW_ADMIN_ROLE) {
        if (newRate > MAX_PROTOCOL_FEE) revert InvalidProtocolFee(newRate);
        _protocolFeeRate = newRate;
    }

    function setProtocolFeeRecipient(address newRecipient) external onlyRole(FLOW_ADMIN_ROLE) {
        require(newRecipient != address(0), "Invalid recipient");
        _protocolFeeRecipient = newRecipient;
    }

    // Emergency functions
    function emergencyWithdraw(address token, address to, uint256 amount)
        external
        onlyRole(ADMIN_ROLE)
    {
        _transferFunds(token, to, amount);
    }

    // Support for ETH contributions
    receive() external payable {
        // Allow ETH deposits
    }
}

